mapper.select:
SELECT ORD_SEQ, ID, TOTAL_PRICE, ORDER_NUM, DELI_INFO, REGDATE, RNAME, ADDRESS1, ADDRESS2, PHONE, CONTENT FROM (SELECT ROW_NUMBER() OVER(ORDER BY REGDATE DESC, ORDER_NUM ASC)AS RNUM, ORD_SEQ, ID, TOTAL_PRICE, ORDER_NUM, DELI_INFO, REGDATE, RNAME, ADDRESS1, ADDRESS2, PHONE, CONTENT FROM ORDER_ WHERE 1=1
AND ID LIKE '%'||#{s_keyword}||'%'
) A WHERE RNUM BETWEEN ${start} AND ${end}

SELECT ORDER_SUB_SEQ, MODEL_ID, COUNT, ORDER_NUM
FROM ORDER_SUB
WHERE ORDER_NUM=#{order_num}

mapper.update:
UPDATE ORDER_ 
SET DELI_INFO=1
WHERE ORD_SEQ=#{ord_seq}
