mapper.select:
SELECT *
FROM MEMBER
WHERE ID=#{id} AND PWD=#{pwd}

SELECT *
FROM MEMBER
WHERE ID=#{id} AND PHONE=#{phone}

SELECT PHONE
FROM MEMBER
WHERE PHONE=#{phone}

mapper.update:
UPDATE MEMBER SET
PWD=#{pwd}, ADDRESS1=#{address1}, ADDRESS2=#{address2}, PHONE=#{phone}
WHERE ID=#{id}

UPDATE MEMBER SET
PWD=#{pwd}, ADDRESS1=#{address1}, ADDRESS2=#{address2}
WHERE ID=#{id}

mapper.select:
select NVL(count(*), 0)
from REVIEW
where id=#{id}

select NVL(COUNT(*), 0)
from QNA
where id=#{id}
